    @Size(min = 2, max = 45)
    @NotNull
    private String name;

    @Size(min = 2, max = 45)
    @NotNull
    private String surname;

    @Past
    private Date dateOfBirth;

    @Size(min = 2, max = 45)
    @NotNull
    private String passportNumber;

    @Size(max = 255)
    private String passportData;

    @Size(max = 255)
    private String address;

